import React from 'react'
import { useFood } from '../context'
import { assets } from '../assets/frontend_assets/assets';

const CartItems = () => {

    const { food_list , cartItems } = useFood();
    console.log(cartItems);

  return (
    <div className=' pt-10' >
        <div className='grid grid-cols-6 mb-10' >
            <div>Items</div>
            <div>Title</div>
            <div>Price</div>
            <div>Quantity</div>
            <div>Total</div>
            <div>Remove</div>
        </div>
        {
            food_list.map( (food ) => {
                if( cartItems[food._id] > 0 ){
                    return (
                        <div>
                            <div className='grid grid-cols-6' >
                                <img className='h-10 w-10'  src={food.image} />
                                <div>{food.name}</div>
                                <div>${food.price}</div>
                                <div>{cartItems[food._id]}</div>
                                <div>${food.price*cartItems[food._id]}</div>
                                <div className='' ><img src={assets.cross_icon} /></div>
                            </div>
                            <div className='border border-slate-400 w-full' ></div>
                        </div>
                    )
                }    
            })
        }
    </div>
  )
}

export default CartItems
