import React from 'react';
import { useFood } from '../context';

const CartItems = () => {
    const { food_list, cartItems } = useFood();
    console.log(cartItems);

    return (
        <div className='grid grid-cols-6 pt-10 bg-gray-500'>
            <div>Items</div>
            <div>Title</div>
            <div>Price</div>
            <div>Quantity</div>
            <div>Total</div>
            <div>Remove</div>
            {
                food_list.map((food) => {
                    if (cartItems[food.id] > 0) {
                        return (
                            <div key={food.id} className='grid grid-cols-6'>
                                <div>{food.name}</div>
                                <div>{food.price}</div>
                                <div>{cartItems[food.id]}</div>
                                <div>{(food.price * cartItems[food.id]).toFixed(2)}</div>
                                <div>
                                    <button onClick={() => {/* Handle removal logic */}}>
                                        Remove
                                    </button>
                                </div>
                            </div>
                        );
                    }
                    return null; // Explicitly return null if condition is not met
                })
            }
        </div>
    );
};

export default CartItems;
